import React, { useState } from 'react';

import './App.css';
import axios from 'axios';

function Form(props) {
  const [form, setForm] = useState({
    fullname:'',
    country:'',
    contact_number:'',
    email_id:'',
    city:'',
  });

 const onChange= (e)=>{
   setForm({
     ...form,
     
     [e.target.name]: e.target.value
  
  
 
})
console.log()
 }

 const onSubmit= (e)=>{
   e.preventDefault();
   const register={
    fullname:form.fullname,
    country:form.country,
    contact_number:form.contact_number,
    email_id:form.email_id,
    city:form.city,

   }

   console.log(register);

   axios.post('http://localhost:5000/register/add', register)
   .then(res => console.log(res.data));
    

 }
  return (
    <div >
      <div>
      <h1>
        <span style={{color:'#CF1322'}}>
          APT 
        </span>
        <span style={{color:'#376EE6'}}>
        &nbsp; GRAND ONLINE SCHOLARSHIP TEST
        </span>
      </h1>
      </div>
      <div>
        <h2>AGST 20-21</h2>
        <p>APT provides opportunity of 100% Scholarship Test <br/>through AGST-APT Grand Scholarship Test,every year</p>
      </div>
      <form onSubmit={onSubmit}>
        <input type="text" required value={form.fullname} name='fullname' onChange={onChange}/>
        <input type="text" required value={form.country} name='country' onChange={onChange} />
        <input type="text" required value={form.contact_number} name='contact_number' onChange={onChange} />
        <input type="text" required value={form.email_id} name='email_id' onChange={onChange} />
        <input type="text" required value={form.city} name='city' onChange={onChange} />
        <button type="submit">sum</button>
      </form>
    </div>
  );
}

export default Form;
