import React, { useState } from 'react';
import TextField from '@material-ui/core/TextField';

import './Form.css'


import axios from 'axios';

function Form(props) {
  const [form, setForm] = useState({
    fullname:'',
    country:'',
    contact_number:'',
    email_id:'',
    city:'',
  });

 const onChange= (e)=>{
   setForm({
     ...form,
     
     [e.target.name]: e.target.value
  
  
 
})
console.log()
 }

 const onSubmit= (e)=>{
   e.preventDefault();
   const register={
    fullname:form.fullname,
    country:form.country,
    contact_number:form.contact_number,
    email_id:form.email_id,
    city:form.city,

   }

   console.log(register);

   axios.post('http://localhost:5000/register/add', register)
   .then(res => console.log(res.data));
    

 }
  return (
    <div >
      <div>
      <h1>
        <span style={{color:'#CF1322'}}>
          APT 
        </span>
        <span style={{color:'#376EE6'}}>
        &nbsp; GRAND ONLINE SCHOLARSHIP TEST
        </span>
      </h1>
      </div>
      <div className='wrapper' styel={{ width:'100%'}}>
      <div className='one'>
        <h2 style={{textAlign:'center',color:'#CF1322'}}>AGST 20-21</h2>
        <p>APT provides opportunity of 100% Scholarship Test <br/>through AGST-APT Grand Scholarship Test,every year</p>
        <p>Its 10th sucessful year of conducting AGST</p>
        <p>Test will be conducted online on 22nd December,2019<br/>
        (Sunday) for Class 10th, 11th, & 12th students @ 1:00pm</p>
        <p>For giving test, get yourself registered on ********** and 
          <br/>you will receive Roll No. & link for AGST
        </p>
        
    </div>
    <div className='two'>
      <form onSubmit={onSubmit}>
      <TextField id="standard-basic" label="Fullname" required value={form.fullname} name='fullname' onChange={onChange} />
      <TextField id="standard-basic" label="Country" required value={form.country} name='country' onChange={onChange} />
      <TextField id="standard-basic" label="Contact number" required value={form.contact_number} name='contact_number' onChange={onChange} />
      <TextField id="standard-basic" label="Email Id" required value={form.email_id} name='email_id' onChange={onChange} />
      <TextField id="standard-basic" label="City" required value={form.city} name='city' onChange={onChange} />

        <input type="text" required value={form.fullname} name='fullname' onChange={onChange}/><br/>
        <input type="text" required value={form.country} name='country' onChange={onChange} /><br/>
        <input type="text" required value={form.contact_number} name='contact_number' onChange={onChange} /><br/>
        <input type="text" required value={form.email_id} name='email_id' onChange={onChange} /><br/>
        <input type="text" required value={form.city} name='city' onChange={onChange} />
        <button type="submit">sum</button>
      </form>
      </div>
      </div>
    </div>
  );
}

export default Form;
